sp_widget.client_script:
function($scope) {
  /* widget controller */
  var c = this;

	$scope.$on("$destroy", function () {
		$('.kb_user_subs_pref').remove();
  });
}

sp_widget.link:
function(scope) {
	if(scope.data.show_menu_entry && !$('.kb_user_subs_pref')[0] && $("a[ng-href*='id=actsub_notif_pref']").length ==0) {
		$('#sp-nav-bar ul li.hidden-xs.dropdown ul.dropdown-menu li:first').after('<li class="kb_user_subs_pref"><a role="link" href="?id=actsub_notif_pref&sysparm_domain_restore=false&sysparm_stack=no">${Notification Settings}</a></li>');
		$('#sp-nav-bar ul li.visible-xs-block:first').after('<li class="visible-xs-block kb_user_subs_pref"><a role="link" href="?id=actsub_notif_pref&sysparm_domain_restore=false&sysparm_stack=no">${Notification Settings}</a></li>');
	}
}

sp_widget.script:
(function() {
	if(input)
		return;
	else {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.show_menu_entry = false;
	data.portal_suffix = $sp.getPortalRecord().getValue('url_suffix');
	data.redirect_url_property = gs.getProperty('sn_km_portal.glide.knowman.serviceportal.portal_url');
	data.knowledge_advanced_enabled = GlidePluginManager.isActive("com.snc.knowledge_advanced");
	data.show_menu_entry = ((data.redirect_url_property == data.portal_suffix) || (data.portal_suffix == 'sp')) && data.knowledge_advanced_enabled;
	}
})();
